mapper.select:
select 
	userId , userPass , userName ,verify
from 
	member
where userId = #{userId}
	and userPass =#{userPass}

mapper.delete:
delete from 
	member 
where 
	userId = #{userId}
 and 
 	userPass = #{userPass}

mapper.select:
select count (*) from 
	member 
where 
	userId = #{userId} 
and 
	userPass = #{userPass}
